 $(document).ready(function(){

 

 	$('.content-box-wrapper').each(function(){  
        var highestBox = 0;

        $(this).find('.content-box').each(function(){
            if($(this).height() > highestBox){  
                highestBox = $(this).height();  
            }
        })

        $(this).find('.content-box').height(highestBox);
    });    



    $("#calculatorBtn").click(function () {

       event.preventDefault();

       $('.calcu-wrapper').fadeIn();
    });
$(".close-calcu").click(function () {
       $('.calcu-wrapper').fadeOut();
    });

	$(".InquireBtn").click(function () {
       $('.fadingForm').fadeIn();
    });
     $(".formClose").click(function () {
       $('.fadingForm').fadeOut();
    });
    $("#colsultantBtn").click(function () {
       $('#colsultantModal').fadeIn();
    });
    $(".close-top").click(function () {
       $('#colsultantModal').fadeOut();
    });


    
 
     var owl = $("#owl-example");
 
  owl.owlCarousel({
      items : 4, //10 items above 1000px browser width
      itemsDesktop : [1000,5], //5 items between 1000px and 901px
      itemsDesktopSmall : [900,3], // betweem 900px and 601px
      itemsTablet: [600,2], //2 items between 600 and 0
      itemsMobile : false // itemsMobile disabled - inherit from itemsTablet option
  });
 
  // Custom Navigation Events
  $(".next").click(function(){

    
    owl.trigger('owl.next');
  })
  $(".prev").click(function(){
    owl.trigger('owl.prev');
  })
  $(".play").click(function(){
    owl.trigger('owl.play',1000); //owl.play event accept autoPlay speed as second parameter
  })
  $(".stop").click(function(){
    owl.trigger('owl.stop');
  })

  

});


var slideHeight = 400;
jQuery(".buzz").each(function() {
    var $this = $(this);
    var $wrap = $this.children(".wrap");
    var defHeight = $wrap.height();
    if (defHeight >= slideHeight) {
        var $readMore = $this.find(".read-more");
        $wrap.css("height", slideHeight + "px");
        $readMore.append("<a href='#'>Read More</i></a>");
        $readMore.children("a").bind("click", function(event) {
            var curHeight = $wrap.height();
            if (curHeight == slideHeight) {
                $wrap.animate({
                    height: defHeight
                }, "normal");
                $(this).text("Close");
                $wrap.children(".gradient").fadeOut();
            } else {
                $wrap.animate({
                    height: slideHeight
                }, "normal");
                $(this).text("Read More");
                $wrap.children(".gradient").fadeIn();
            }
            return false;
        });
    }
});
